import React, { useState } from 'react';
import {plantList} from '../data/plantList'
import CategorieList from "../CategorieList/"

import { AiFillFire, AiOutlineFire } from 'react-icons/ai';

import './index.css';

function Product({cart, setCart}){

    const [filter, setFilter] = useState("");
    
    const products = plantList;
    const categories = plantList.reduce((acc, plant) => 
         acc.includes(plant.category) ? acc : acc.concat(plant.category), []
    )

    const handleClick = (product) => {
        const actual = cart;
        const newCart = actual.concat(product);
        setCart(newCart);
    }

    return (
        <>
            <div className="productCategorie">

                <CategorieList categories={categories} filter={filter} setFilter={setFilter}/>

                <ul className="productContainer">
                    {
                        products.map((product) => {
                            return (filter === product.category) || (filter === "") ? 
                            (
                                <li key={product.id}>
                                    <div className="price">{product.price} â‚¬</div>
                                    <img src={product.img} alt={`${product.name}-img`} />
                                    {product.name}  {product.isBestSale ? <AiFillFire className="fillIcon" /> : <AiOutlineFire />} 
                                    <button className="productButton" onClick={() => handleClick(product)}>+Ajouter
                                    </button>
                                </li>
                            ) 
                                : null
                        })
                    }
                </ul>
            </div>
        </>
    )
}

export default Product;
