import axios from "axios";
import React, {useEffect, useState} from "react"
import { useParams} from "react-router-dom"

function UpdateProduct(){

    const[category, setCategory] = useState([])
    const[prod,setProd] = useState({})

    const {ID} = useParams();

    useEffect(() => {
        axios.get("http://127.0.0.1/code/iplant/?action=getCategory").then((resp) => {
            setCategory(resp.data);
        })

        axios.get(`http://127.0.0.1/code/iplant/?action=getProduct&ID=${ID}`).then((resp) => {
            setProd(resp.data);
        })
    }, [ID])

    return (
        <>
            <form >
                    <input type="text" value={prod.name}/>

                    <input type="text" value={prod.price}/>

                    <select value={prod.category}>
                        <option key="null" value="">-------</option>
                        {
                            category.map((cat) => {
                                return (
                                    <option key={cat.ID} value={cat.ID}>{cat.nom}</option>
                                )
                            })
                        }
                    </select>

                    <input type="checkbox" value={prod.isBestSale} checked={prod.isBestSale ? true : false}/>

                    <input type="text" value={prod.img}/>

                    <input type="submit" value="Enregistrer" />
            </form>
        </>
    );

}

export default UpdateProduct
